let retries = msg.retries || 0;
let delay = Math.pow(2, retries) * 1000; // 1s, 2s, 4s...

if (retries < 5) {
    msg.retries = retries + 1;
    setTimeout(() => {
        node.send(msg);
    }, delay);
} else {
    msg.payload = {error: "Max retries reached"};
    node.error("Max retries reached", msg);
}

const cacheKey = msg.topic;
const cachedValue = flow.get(cacheKey);

if (cachedValue) {
    msg.payload = cachedValue;
    msg.cached = true;
    return [msg, null];
} else {
    return [null, msg];
}

const cacheKey = msg.topic;
flow.set(cacheKey, msg.payload);
return msg;

let failures = context.get('failures') || 0;
const maxFailures = 3;
const resetTimeout = 60000; // 1 minuto

if (msg.payload.error) {
    failures += 1;
    context.set('failures', failures);

    if (failures >= maxFailures) {
        context.set('circuit_open', true);
        node.status({fill: "red", shape: "ring", text: "circuito aberto"});
        setTimeout(() => context.set('circuit_open', false), resetTimeout);
    }
    return null;
} else {
    context.set('failures', 0);
    node.status({});
    return msg;
}

// Randomly throw an error rather than
// pass on message.
if (Math.random() < 0.5) {
   node.error("a random error", msg);
} else {
    return msg;
}

// Find demo cookies and clear them
var cookieNames = Object.keys(msg.req.cookies).filter(function(cookieName) { return /^demo-/.test(cookieName);});
msg.cookies = {};

cookieNames.forEach(function(cookieName) {
    msg.cookies[cookieName] = null;
});

return msg;

var params = [];
for (var i in msg.payload) {
    params.push(encodeURIComponent(i)+"="+encodeURIComponent(msg.payload[i]));
}
msg.querystring = params.join("&");

return msg;
